import React from 'react';
import { StyleSheet, Text, View, TextInput, Button, FlatList, ScrollView } from 'react-native';

export default function App() {

  const addButton = () => disp( [...list, 'Grupo ' + parseInt(list.length+1)+ ': ' + value + '\n Integrantes: 0/5'] );

  return (
    <>
      <View style={stylesTextInput.container}>
          <TextInput
            style={stylesTextInput.textInput}
          />
      </View>
      <View style={stylesFooter.container}>
        <Button 
          style={stylesFooter.buttonAdd}
          title="    Search    "
          color="#7c52a8"
          onPress={addButton}
        />
      </View> 
    </>
  );
}

const DisplayAnImage = () => {
  return (
    <View style={styles.container}>

      <Image
        style={styles.tinyLogo}
        source={{
          uri: 'http://pm1.narvii.com/7274/04cd093750e53c5c8e751f3a5d47020533c2951er1-556-750v2_uhq.jpg',
        }}
      />

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F6F8',
  },
  elements: {
    justifyContent: 'center',
    padding: 40
  },
  tinyLogo: {
    width: 556,
    height: 750,
  },
});



const stylesTextInput = StyleSheet.create({
  container: {
    flex: 1,
    padding: 15,
  },
  textInput:{
    height: 40,
    textAlign: 'center', 
    borderColor: '#156cdb', 
    borderWidth: 2,
  }
});

const stylesFooter = StyleSheet.create({
  container: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    paddingBottom: 15
  },
  buttonAdd: {
    flex:1,
  },
  buttonDelete: {
    flex:1,
  }
});
