import { StatusBar } from 'expo-status-bar';
import React, {Component} from 'react';

import { StyleSheet, Text, View, TextInput, Button, FlatList, ScrollView, Image } from 'react-native';

export default class calculateRoute extends Component{
  constructor () { 
    super();
    this.state = {showImage: false};
  }

  showImageFunc = () => {
     this.setState({showImage: true});
  }

  render() {
    return (
      <View style={styles.container}>
        <View style={styles.top}>
        <TextInput
          style={stylesTextInput.textInput}
        />
      </View>
        {this.state.showImage &&
        <View style={stylesTextInput.container}>
          <Image
            style={{width: 185 , height: 250}}
            source={{
              uri: 'http://pm1.narvii.com/7274/04cd093750e53c5c8e751f3a5d47020533c2951er1-556-750v2_uhq.jpg',
            }} />
        </View>}
        <Button
          style={stylesTextInput.textInput}
          onPress={this.showImageFunc}
          title="   Search   "
          color="#841584"
  />
</View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "space-between",
    backgroundColor: "#fff",
    padding: 20,
    margin: 10,
  },
  top: {
    flex: 0.3,
    backgroundColor: "grey",
    borderWidth: 5,
    borderTopLeftRadius: 20,
    borderTopRightRadius: 20,
  },
  middle: {
    flex: 0.3,
    backgroundColor: "beige",
    borderWidth: 5,
  },
  bottom: {
    flex: 0.3,
    backgroundColor: "pink",
    borderWidth: 5,
    borderBottomLeftRadius: 20,
    borderBottomRightRadius: 20,
  },
});




const stylesTextInput = StyleSheet.create({
  padding: 30,
  container: {
    flex: 1,
    padding: 15,
  },
  textInput:{
    height: 40,
    textAlign: 'center', 
    borderColor: '#156cdb', 
    borderWidth: 2,
  }
});

const stylesFooter = StyleSheet.create({
  container: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    paddingBottom: 15
  },
  buttonAdd: {
    flex:1,
  },
  buttonDelete: {
    flex:1,
  }
});
