import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Component } from 'react';
import { StyleSheet, Text, View, TextInput, Button, FlatList, ScrollView, Image } from 'react-native';

class App extends Component {

  constructor() {
    super();
    this.state = {
        imgOpacity: 0
    }
}

toggleImage() {
    if ( this.state.imgOpacity === 1 ) {
        this.setState({
            imgOpacity: 0
        })    
    } else {
        this.setState({
            imgOpacity: 1
        }) 
    }
}

render(){
    return (
      <>
        <View style={stylesTextInput.container}>
            <TextInput
              style={stylesTextInput.textInput}
            />
        </View>
        <View style={styles.container}>
        <Image
          style={styles.tinyLogo}
          source={{
            uri: '',
          }}
        />
      </View>
        <View style={stylesFooter.container}>
        <Button title="click me"
                    onPress= {this.toggleImage}
                /> 
        </View> 
      </>
    );
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F6F8',
  },
  elements: {
    justifyContent: 'center',
    padding: 40
  },
  tinyLogo: {
    width: 278,
    height: 375,
  },
});



const stylesTextInput = StyleSheet.create({
  container: {
    flex: 1,
    padding: 15,
  },
  textInput:{
    height: 40,
    textAlign: 'center', 
    borderColor: '#156cdb', 
    borderWidth: 2,
  }
});

const stylesFooter = StyleSheet.create({
  container: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    paddingBottom: 15
  },
  buttonAdd: {
    flex:1,
  },
  buttonDelete: {
    flex:1,
  }
});
