import { StatusBar } from 'expo-status-bar';
import React, {Component, useState} from 'react';

import { StyleSheet, Text, View, TextInput, Button, Image } from 'react-native';
import axios from 'axios';

var level, id, kills, rank, legend, image;

const getProfileData = async(usuario) =>{
  let response = await axios.get(
    `https://public-api.tracker.gg/v2/apex/standard/profile/xbl/${usuario}`,
    {
      headers: {
        "TRN-Api-Key": "c5890f7f-c905-4a25-b1fc-b2b9ec53d9b9"
      }
    }
  )
  id = response.data.data.platformInfo.platformUserId
  level = response.data.data.segments[0].stats.level.value;  //console.log(response.data.data.segments[0].stats.level.value)
  kills = response.data.data.segments[0].stats.kills.value
  rank = response.data.data.segments[0].stats.rankScore.metadata.rankName
  image = response.data.data.segments[1].metadata.imageUrl
  legend =  response.data.data.metadata.activeLegendName
}

export default function App() {

  const [perfil, setPerfil] = useState('');

  const profileHandler = (val) => {
    setPerfil(val);
  }

  function wait(ms){
    var start = new Date().getTime();
    var end = start;
    while(end < start + ms) {
      end = new Date().getTime();
   }
 }


    return (
      <View style={styles.container}>
        <View>
        <TextInput
          style={stylesTextInput.textInput}
          onChangeText={profileHandler}
        />
      </View>
          <Text> ID: {id} </Text>
          <Text> Level: {level} </Text>
          <Text> Legend: {legend} </Text>
          <Text> Kills: {kills} </Text>
          <Text> Rank: {rank} </Text>
        <View>
          <Button
            style={stylesTextInput.textInput}
            onPress={()=>{getProfileData(perfil); wait(1000); this.setState()}}
            title="   Search   "
            color="#841584"
          />
        </View>
      </View>
    );

}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "space-between",
    padding: 20,
    margin: 10,
  },
  top: {
    flex: 0.3,
  },
  middle: {
    flex: 1,
  },
  bottom: {
    flex: 0.2,
  },
});

const stylesTextInput = StyleSheet.create({
  padding: 30,
  container: {
    flex: 1,
    padding: 15,
  },
  textInput:{
    height: 40,
    textAlign: 'center', 
    borderColor: '#156cdb', 
    borderWidth: 2,
  }
});
